FilterRequest.prototype.media = function (_username) {

  //var that = this;
    return new WebRequest( )
        .setMethod('GET')
        .setResource('mediaInfoAnonym', {username: _username, maxId: ''}) //   userInfoAnonym
        .setJSONEndpoint()
        .setHeaders({
            'Host': CONSTANTS.HOSTNAME,
            'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,*;q=0.8',
            'Accept-Language': 'en-us',
            'Content-Type': 'application/x-www-form-urlencoded',
            'Origin': ORIGIN,
            'Connection': 'keep-alive',
            'User-Agent': iPhoneUserAgent,
          //  'Referer': that.error.url
        })
        // .setBodyType('form')
        // .removeHeader('x-csrftoken') // we actually sending this as post param
        // .setData({
        //     response_code: code,
        //     csrfmiddlewaretoken: that.session.CSRFToken
        // })
        .send({followRedirect: true}) // false
        .then(function(response) {
            return new Promise((resolve, reject) => {
                resolve(response);
            });
        });
}

FilterRequest.prototype.getUser = function (_username) {

  //var that = this;
    return new WebRequest( )
        .setMethod('GET')
        .setResource('userInfoAnonym', {username: _username}) //   userInfoAnonym
        .setJSONEndpoint()
        .setHeaders({
            'Host': CONSTANTS.HOSTNAME,
            'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,*;q=0.8',
            'Accept-Language': 'en-us',
            'Content-Type': 'application/x-www-form-urlencoded',
            'Origin': ORIGIN,
            'Connection': 'keep-alive',
            'User-Agent': iPhoneUserAgent,
          //  'Referer': that.error.url
        })
        // .setBodyType('form')
        // .removeHeader('x-csrftoken') // we actually sending this as post param
        // .setData({
        //     response_code: code,
        //     csrfmiddlewaretoken: that.session.CSRFToken
        // })
        .send({followRedirect: true}) // false
        .then(function(response) {
            var json_obj = new Object();
            json_obj.followerCount = response.user.follows.count;
            json_obj.followingCount = response.user.followed_by.count;
            json_obj.mediaCount = response.user.media.count;
            json_obj.isPrivate = response.user.is_private;
            json_obj.fullName = response.user.full_name;
            json_obj.biography = response.user.biography;
            json_obj.username = response.user.username;
            json_obj.id = response.user.id;
            return new Promise((resolve, reject) => {
                resolve(json_obj);
            });
        });
}

